import Card from "../../GenericComponents/Card/Card";
import { useSelector } from "react-redux";
import "./People.scss";
import Modals from "../../GenericComponents/Modal/Modals";
import { useModal } from "../../hooks/useModal";
import { useState } from "react";

const People = () => {
  const people = useSelector((state) => state.people);
  const [selectedPerson, setSelectedPerson] = useState(null);
  const [isOpenModal1, openModal1, closeModal1] = useModal(false);

  const handleOpenModal = (person) => {
    setSelectedPerson(person);
    openModal1();
  };

  return (
    <div className="main-container">
      {people.map((person) => {
        return (
          <Card
            key={person._id}
            name={person.name}
            films={person.films}
            birth_year={person.birth_year}
          >
            <button onClick={() => handleOpenModal(person)}>OpenDetail</button>
          </Card>
        );
      })}
      {isOpenModal1 && selectedPerson && (
        <Modals isOpen={isOpenModal1} closeModal={closeModal1} >
          {selectedPerson}
        </Modals>
      )}
    </div>
  );
};

export default People;
