import Card from "../../GenericComponents/Card/Card";
import { useSelector } from 'react-redux';
// import './People.scss'
import Modals from "../../GenericComponents/Modal/Modals";
import { useModal } from "../../hooks/useModal";
import React, { useState, useEffect } from "react";
import Pagination from "../../GenericComponents/Pagination/Pagination";
import SearchBar from "../../GenericComponents/SearchBar/SerchBar";
import {useDispatch } from 'react-redux';

const Planets = ()=>{
    const planets = useSelector((state) => state.planets);
    const searchResults = useSelector((state) => state.searchResults.planets);
    const [selectedPlanet, setSelectedPlanet] = useState(null);
    const [isOpenModal, openModal, closeModal] = useModal(false);
    const [currentPage, setCurrentPage] = useState(1);
    const [planetPerPage, setPlanetPerPage] = useState(8);
    const [indexOfLastPlanet, setIndexOfLastPlanet] = useState(planetPerPage);
    const [indexOfFirstPlanet, setIndexOfFirstPlanet] = useState(0);

    useEffect(() => {
      setCurrentPage(1); // Reset currentPage when searchResults change to show first page of search results
      const newIndexOfLastPlanet = 1 * planetPerPage;
      const newIndexOfFirstPlanet = newIndexOfLastPlanet - planetPerPage;
      setIndexOfLastPlanet(newIndexOfLastPlanet);
      setIndexOfFirstPlanet(newIndexOfFirstPlanet);
    }, [searchResults, planetPerPage]);

    const currentPlanet = (searchResults && searchResults.length > 0) ? searchResults.slice(indexOfFirstPlanet, indexOfLastPlanet) : planets.slice(indexOfFirstPlanet, indexOfLastPlanet);
    console.log(currentPlanet)
    const handleOpenModal = (planet) => {
      setSelectedPlanet({...planet, isPlanet: true});
      openModal();
    };

    const pagination = (pageNumber) => {
      setCurrentPage(pageNumber);
    };

    return (
      <>
      <div className="main-container">
      <SearchBar prop='planets'></SearchBar>
        { (currentPlanet).map((planet) => {
          return (
            <Card
              key={planet._id}
              name={planet.name}

            >
              <button onClick={() => handleOpenModal(planet)}>OpenDetail</button>
            </Card>
          );
        })}
        {isOpenModal && selectedPlanet && (
          <Modals isOpen={isOpenModal} closeModal={closeModal} >
            {selectedPlanet}

          </Modals>
        )}
      </div>
      <Pagination
          propPerPage={planetPerPage}
          length={planets.length}
          pagination={pagination}
          currentPage={currentPage}
        />
      </>
    );
  };


export default Planets;
