import { GET_PLEOPLE, GET_PLANETS, GET_VEHICLES, GET_FILMS  } from "./actions";


const initialState = {
    people : [],
};

const rootReducer = (state = initialState, action)  => {
    switch(action.type){
        case GET_PLEOPLE: 
        return { ...state, people: action.payload};

        default: return { ...state };
    }
};

export default rootReducer;
