import React, { useEffect, useState } from "react";
import Card from "../../GenericComponents/Card/Card";
import { useDispatch, useSelector } from 'react-redux';
import "./People.scss";
import Modals from "../../GenericComponents/Modal/Modals";
import { useModal } from "../../hooks/useModal";
import { getAllPeople } from "../../redux/actions";

const People = () => {
  const people = useSelector((state) => state.people);
  const total = useSelector((state) => state.total);
  const loading = useSelector((state) => state.loading);
  const [selectedPerson, setSelectedPerson] = useState(null);
  const [isOpenModal, openModal, closeModal] = useModal(false);
  const [currentPage, setCurrentPage] = useState(0);

  const dispatch = useDispatch();

  useEffect(() => {
    // Llamar al action con el valor de página actual
    dispatch(getAllPeople(currentPage));
  }, [dispatch, currentPage]);

  const totalPages = total;

  // Mostrar "Loading..." mientras los datos se están obteniendo del backend
  if (loading) {
    return <div>Loading...</div>;
  }

  if (!people || people.length === 0) {
    return <div>No data available.</div>;
  }

  const handlePreviousPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 0));
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => Math.min(prevPage + 1, totalPages - 1));
  };


  const handleOpenModal = (person) => {
    setSelectedPerson({ ...person, isPerson: true });
    openModal();
  };



  return (
    <div >
    <div className="main-container">
      {people.map((person) => {
        return (
          <Card
            key={person._id}
            name={person.name}
          >
            <button onClick={() => handleOpenModal(person)}>OpenDetail</button>
          </Card>
        );
      })}
      {isOpenModal && selectedPerson && (
        <Modals isOpen={isOpenModal} closeModal={closeModal} >
          {selectedPerson}
        </Modals>
      )}

      {/* Renderizar los botones de paginación */}
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 0}>Previous</button>
        <span>{currentPage + 1} / {totalPages}</span>
        <button onClick={handleNextPage} disabled={currentPage === totalPages - 1}>Next</button>
      </div>
    </div>
    </div>
  );
}

export default People;
