import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./NavBar.scss";
import { connect } from "react-redux";
import { searchItems } from "../../redux/actions";
import {useDispatch, useSelector } from 'react-redux';


const NavBar = () => {
  const [searchQuery, setSearchQuery] = useState("");

  const handleSearch = () => {
    // Utiliza una función asincrónica dentro de handleSearch
    (async () => {
      try {
        const searchResults = await searchItems(searchQuery);
        console.log("Resultados de búsqueda:", searchResults);
      } catch (error) {
        console.error('Error al buscar elementos:', error);
      }
    })();
  };


//   const dispatch = useDispatch();

//   useEffect(()=>{
//       dispatch(searchItems())
//   },[dispatch])


  return (
    <div className="main-container">
      <Link to="/home" className="link">
        Home
      </Link>
      <Link to="/contact" className="link">
        Contact
      </Link>
      <div>
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Buscar</button>
      </div>
    </div>
  );
};

export default connect(null, { searchItems })(NavBar);
