import React, { useState } from "react";
import Card from "../../GenericComponents/Card/Card";
import { useSelector } from "react-redux";
import "./People.scss";
import Modals from "../../GenericComponents/Modal/Modals";
import { useModal } from "../../hooks/useModal";
import Pagination from "../../GenericComponents/Pagination/Pagination";
import SearchBar from "../../GenericComponents/SearchBar/SerchBar";

const People = () => {
  const searchResults = useSelector((state) => state.searchResults.people);
  const people = useSelector((state) => state.people);
  const peopleResults = searchResults && searchResults.length ? searchResults : people;

  const [selectedPerson, setselectedPerson] = useState(null);
  const [isOpenModal, openModal, closeModal] = useModal(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [personPerPage, setPersonPerPage] = useState(8);
  const indexOfLastVehicle = currentPage * personPerPage;
  const indexOfFirstVehicle = indexOfLastVehicle - personPerPage;
  const currentPerson = peopleResults ? peopleResults.slice(indexOfFirstVehicle, indexOfLastVehicle) : people.slice(indexOfFirstVehicle, indexOfLastVehicle);


  /**
   * 
   *  {    const [currentPage, setCurrentPage] = useState(1);
    const [vehiclePerPage, setVehiclePerPage] = useState(8);
    const indexOfLastVehicle = currentPage * vehiclePerPage;
    const indexOfFirstVehicle = indexOfLastVehicle - vehiclePerPage;
    const currentVehicle = vehicles.slice(indexOfFirstVehicle, indexOfLastVehicle);} person 
   */

  const handleOpenModal = (person) => {
    setselectedPerson({ ...person, isPerson: true });
    openModal();
  };

  const pagination = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>

    <div className="main-container">
      <SearchBar prop="people"></SearchBar>
      {currentPerson.map((person) => {
        console.log(currentPerson)
        return (
          <Card key={person._id} name={person.name}>
            <button onClick={() => handleOpenModal(person)}>OpenDetail</button>
          </Card>
        );
      })}
      {isOpenModal && selectedPerson && (
        <Modals isOpen={isOpenModal} closeModal={closeModal}>
          {selectedPerson}
        </Modals>
      )}
    </div>

      <Pagination
          propPerPage={personPerPage}
          length={people.length}
          pagination={pagination}
          currentPage={currentPage}
        />
    </div>
  );
};

export default People;
