import { GET_PEOPLE, GET_PLANETS, GET_VEHICLES, GET_FILMS,   } from "./actions";


const initialState = {
    people : [],
    films: [],
    planets: [],
    vehicles: [],
    peopleId: [],
    totalPages: 0, // Si el backend proporciona esta información
    loading: true, // Agrega una propiedad para indicar que se están obteniendo los datos
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_PEOPLE:
      return {
        ...state,
        results: action.payload.results, // Asegúrate de almacenar los datos en results
        totalPages: action.payload.totalPages, // Asegúrate de almacenar totalPages si está disponible
        loading: false, // Indica que ya se obtuvieron los datos
      };
    case GET_FILMS:
      return { ...state, films: action.payload };
    case GET_PLANETS:
      return { ...state, planets: action.payload };
    case GET_VEHICLES:
      return { ...state, vehicles: action.payload };
    default:
      return { ...state };
  }
};

export default rootReducer;
