import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { getPeopleById } from '../../redux/actions';
import Card from '../../GenericComponents/Card/Card';

const Detail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getPeopleById(id));
  }, [dispatch, id]);

  const person = useSelector((state) => state.peopleId);

  return (
    <div className="main-container">
      {person && ( // Aseguramos que person tenga valor antes de mostrar los detalles
        <Card
          name={person.name}
          films={person.films}
          birth_year={person.birth_year}
        />
      )}
    </div>
  );
};

export default Detail;
