import { GET_PLEOPLE, GET_PLANETS, GET_VEHICLES, GET_FILMS  } from "./actions";


const initialState = {
    people : [],
    films: [],
    planets: [],
    vehicles: []
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_PLEOPLE:
      return { ...state, people: action.payload };
    case GET_FILMS:
      return { ...state, films: action.payload };
    case GET_PLANETS:
      return { ...state, planets: action.planets };
    case GET_VEHICLES:
      return { ...state, vehicles: action.payload };
    default:
      return { ...state };
  }
};

export default rootReducer;
