import Card from "../../GenericComponents/Card/Card";
import { useSelector } from "react-redux";
import "./People.scss";
import Modals from "../../GenericComponents/Modal/Modals";
import { useModal } from "../../hooks/useModal";

const People = () => {
  const people = useSelector((state) => state.people);

  const [isOpenModal1, openModal1, closeModal1] = useModal(false);

  return (
    <div className="main-container">
      {people.map((person) => {
        return (
          <Card
            name={person.name}
            films={person.films}
            birth_year={person.birth_year}
          >
            <button onClick={openModal1}>OpenDetail</button>
          </Card>
        );
      })}
      <Modals isOpen={isOpenModal1} closeModal={closeModal1}>
        <h1>MODAL</h1>
        <div>este es mi modal</div>
        <img src="https://loremflickr.com/400/400" alt="nature" />
      </Modals>
    </div>
  );
};

export default People;
