import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { getAllPeople, getAllPlanets, getAllVehicles, getAllFilms } from "../../redux/actions";
import HomeCardMenu from "../../GenericComponents/CardMenu/HomeCardMenu";
import './Home.scss'

const Home = () => {
  const dispatch = useDispatch();
  const people = useSelector((state) => state.people); // Obtén los datos de personas del estado de Redux

  const [currentPage, setCurrentPage] = useState(0);
  const [itemsPerPage, setItemsPerPage] = useState(6);

  useEffect(() => {
    dispatch(getAllPeople(currentPage, itemsPerPage));
    dispatch(getAllFilms());
    dispatch(getAllVehicles());
    dispatch(getAllPlanets());
  }, [dispatch, currentPage, itemsPerPage]);

  const totalPages = Math.ceil(people.length / itemsPerPage);

  const handlePreviousPage = () => {
    setCurrentPage((prevPage) => Math.max(prevPage - 1, 0));
  };

  const handleNextPage = () => {
    setCurrentPage((prevPage) => Math.min(prevPage + 1, totalPages - 1));
  };

  return (
    <div className="sky">
      <HomeCardMenu />
      {/* Renderizar los botones de paginación */}
      <div className="pagination">
        <button onClick={handlePreviousPage} disabled={currentPage === 0}>Previous</button>
        <span>{currentPage + 1} / {totalPages}</span>
        <button onClick={handleNextPage} disabled={currentPage === totalPages - 1}>Next</button>
      </div>
    </div>
  );
}

export default Home;
