import React, { useState } from 'react';
import Card from "../../GenericComponents/Card/Card";
import './People.scss';
import { useSelector } from 'react-redux';


const People = () => {
  const people = useSelector((state) => state.people);
  const [selectedPerson, setSelectedPerson] = useState(null);

  const openModal = (person) => {
    setSelectedPerson(person);
  };

  const closeModal = () => {
    setSelectedPerson(null);
  };

  return (
    <div className="main-container">
      {people.map((person) => (
        <div key={person._id}>
          <Card
            name={person.name}
            films={person.films}
            birth_year={person.birth_year}
            onClick={() => openModal(person)} // Agregar un onClick para abrir el modal
          />
        </div>
      ))}
      {selectedPerson && (
        // Renderizar el modal solo si selectedPerson tiene valor
        <div className="modal-overlay">
          <div className="modal">
            <button className="close-button" onClick={closeModal}>
              X
            </button>
            <Card
              name={selectedPerson.name}
              films={selectedPerson.films}
              birth_year={selectedPerson.birth_year}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default People;
