import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { getPeopleById } from './../../redux/actions';
import Card from '../../GenericComponents/Card/Card';

const Detail = () => {
    const { id } = useParams();
    const dispatch = useDispatch();

    console.log(id)
    useEffect(() => {
      // Llama a la acción para cargar los detalles de la persona cuando el componente se monte
      dispatch(getPeopleById(id));
    }, [dispatch, id]);

    const person = useSelector((state) => state.peopleId); // Asegúrate de ajustar el selector según la estructura de tu store

    return (
        <div className="main-container">
          {person.map((person) => (

              <Card
                name={person.name}
                films={person.films}
                birth_year={person.birth_year}
              />

          ))}
        </div>
    )
  };




export default Detail;
