import React from "react";

const Pagination = ({ propPerPage, length, pagination, currentPage }) => {
  const pageNumber = [];

  for (let i = 1; i <= Math.ceil(length / propPerPage); i++) {
    pageNumber.push(i);
  }

  const visiblePages = 5;

  const renderPageNumbers = () => {
    const pageNumbers = [];

    // ... resto del código anterior ...

    // Mostrar puntos suspensivos si no estamos cerca de la primera página
    if (currentPage > Math.ceil(visiblePages / 2) + 1) {
      pageNumbers.push(
        <li key="first">
          <button onClick={() => pagination(1)}>1</button>
        </li>
      );

      if (currentPage > Math.ceil(visiblePages / 2) + 2) {
        pageNumbers.push(
          <li key="dots1">
            <span>...</span>
          </li>
        );
      }
    }

    // Calcular el rango de páginas que queremos mostrar
    let startPage = Math.max(currentPage - Math.floor(visiblePages / 2), 1);
    let endPage = Math.min(startPage + visiblePages - 1, pageNumber.length);

    // Mostrar botón "First Page" si no estamos en la primera página
    if (currentPage !== 1 && startPage !== 1) {
      pageNumbers.push(
        <li key="first">
          <button onClick={() => pagination(1)}>1</button>
        </li>
      );
    }

    // Mostrar puntos suspensivos si no estamos cerca de la primera página
    if (currentPage > Math.ceil(visiblePages / 2) + 1) {
      if (currentPage > Math.ceil(visiblePages / 2) + 2) {
        pageNumbers.push(
          <li key="dots1">
            <span>...</span>
          </li>
        );
      }

      // Mostrar páginas ocultas entre la primera página y la página actual
      for (let i = startPage + 1; i < currentPage; i++) {
        pageNumbers.push(
          <li key={i}>
            <button onClick={() => pagination(i)}>{i}</button>
          </li>
        );
      }
    }

    // Calcular el rango de páginas que queremos mostrar
    startPage = Math.max(currentPage - Math.floor(visiblePages / 2), 1);
    endPage = Math.min(startPage + visiblePages - 1, pageNumber.length);

    for (let i = startPage; i <= endPage; i++) {
      pageNumbers.push(
        <li key={i} className={i === currentPage ? "active" : ""}>
          <button onClick={() => pagination(i)}>{i}</button>
        </li>
      );
    }

    // Mostrar puntos suspensivos si no estamos cerca de la última página
    if (currentPage < pageNumber.length - Math.ceil(visiblePages / 2)) {
      if (currentPage < pageNumber.length - Math.ceil(visiblePages / 2) - 1) {
        pageNumbers.push(
          <li key="dots2">
            <span>...</span>
          </li>
        );
      }

      // Mostrar páginas ocultas entre la página actual y la última página
      for (let i = currentPage + 1; i <= pageNumber.length; i++) {
        pageNumbers.push(
          <li key={i}>
            <button onClick={() => pagination(i)}>{i}</button>
          </li>
        );
      }

      pageNumbers.push(
        <li key="last">
          <button onClick={() => pagination(pageNumber.length)}>
            {pageNumber.length}
          </button>
        </li>
      );
    }

    // ... resto del código anterior ...

    return pageNumbers;
  };

  return (
    <div>
      <ul>{renderPageNumbers()}</ul>
    </div>
  );
};

export default Pagination;
