import axios from "axios";

export const GET_PLEOPLE = "GET_PLEOPLE"
export const GET_PLANETS = "GET_PLANETS"
export const GET_VEHICLES = "GET_VEHICLES"
export const GET_FILMS = "GET_FILMS"

//Section PEOPLE
export const getAllPeople = () => {
   return async function(dispatch){
    const apiData = await axios.get("http://localhost:5001/people")

    const user = apiData.data;
      console.log(user)
    dispatch({type: GET_PLEOPLE, payload: user})
   }
};

//Section PLANETS
export const getAllPlanets = (id) => {
   return async function(dispatch){
    const apiData = await axios.get(`http://localhost:5001/planets/${id}`)

    const planets = apiData.data;

    dispatch({type: GET_PLANETS, payload: planets})
   }
};


//Section VEHICLES
export const getAllVehicles = () => {
   return async function(dispatch){
    const apiData = await axios.get("http://localhost:5001/vehicles")

    const user = apiData.data;

    dispatch({type: GET_VEHICLES, payload: user})
   }
};


//Section FILMS
export const getAllFilms = () => {
   return async function(dispatch){
    const apiData = await axios.get("http://localhost:5001/films")

    const user = apiData.data;

    dispatch({type: GET_FILMS, payload: user})
   }
};
