import React from "react";

export default function Pagination({propPerPage, length, pagination}){
    const pageNumber = [];

    for(let i = 1; i <= Math.ceil(length / propPerPage); i++){
        pageNumber.push(i)
    }
   const firstPage = pageNumber.slice(0,1)
   console.log(firstPage)

    return(
        <div >
            <ul >
                {pageNumber  && pageNumber.map(number =>{
                    return(
                    <span key = {number} >
                    <button onClick = {()=> pagination(number)} > {number} </button>
                    </span>)
                })}
            </ul>
        </div>
    )
}
