import { GET_PEOPLE, GET_PLANETS, GET_VEHICLES, GET_FILMS,   } from "./actions";


const initialState = {
    people : [],
    films: [],
    planets: [],
    vehicles: [],
    peopleId: [],
    total: 0, 
    loading: true, // Agrega una propiedad para indicar que se están obteniendo los datos
};

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_PEOPLE:
      return {
        ...state,
        loading: false,
        people: action.payload.results, 
        total: action.payload.total, 
      };
    case GET_FILMS:
      return { ...state, films: action.payload };
    case GET_PLANETS:
      return { ...state, planets: action.payload };
    case GET_VEHICLES:
      return { ...state, vehicles: action.payload };
    default:
      return { ...state };
  }
};

export default rootReducer;
